! Check that intent(in out***REMOVED*** translates as intent(inout***REMOVED***.
! The separation seems to be a common usage.
      subroutine foo(x***REMOVED***
          implicit none
          real(4***REMOVED***, intent(in out***REMOVED*** :: x
          dimension x(3***REMOVED***
          x(1***REMOVED*** = x(1***REMOVED*** + x(2***REMOVED*** + x(3***REMOVED***
          return
      end
